import React, {useState, useEffect} from 'react';
import './App.css';

function App() {


  const[newItem, setNewItem] = useState("");
  const[items, setItems] = useState([]);
  const[tasksRemaining, setTasksRemaining] = useState(0);

  useEffect(() => {setTasksRemaining(items.filter(item => !item.completed).length) });


  const DeleteAll = () => {
    setItems([])
  }



  function addItem(){

    if(!newItem){
      alert("Please enter a task.")
      return;
    }



    const item = {
      id: Math.floor(Math.random() * 1000),
      value: newItem
    };

      setItems(oldList => [...oldList, item]);
      setNewItem("");

      console.log(items);

  }

  return (
      <div className='App'>
      <header className="App-header">
        <h1> Daily To-Do List</h1>
        <div class = "header"> ({tasksRemaining}) daily tasks left</div>

        <input
        type= "text"
        placeholder= 'Add task'
        value= {newItem}
        onChange={e => setNewItem(e.target.value)}
        />
        <button onClick={() => addItem()}>Add</button>
        <button onClick={() => DeleteAll()}>Clear All</button>

        <ul>
          {items.map(item => {
            return(
              <li key={item.id}>{item.value}<input type = "checkbox" style = {{ textDecoration: item.done ? "line-through" : ""}}></input> 
            </li>
            )
          })}
        </ul>
      </header>
    </div>
  );
};

export default App;
